-----------SCRIPT BUSCAR PUBLICACIONES DE UN USUARIO EN BASE AL CODIGO -------------
SELECT
   usuario.cod_usuario,
   usuario.nombre
   publicacion.cod_publicacion,
   publicacion.nombre,
   publicacion.descripcion,
   publicacion.posicion_x,
   publicacion.posicion_y,
   publicacion.estado,
   asignacion.fecha,
   asignacion.hora,
FROM
   publicacion
INNER JOIN asignacion ON publicacion.cod_publicacion=asignacion.cod_publicacion
INNER JOIN usuario ON usuario.cod_usuario=asignacion.cod_usuario
where usuario.cod_usuario=30;

